mapper.insert:
insert into USERTB (USER_ID, USER_EMAIL, USER_PHONE,
USER_PASSWORD, SHOP_SEQ,
USER_NAME, USER_ADDRESS, USER_LEVEL
)
values (#{userId,jdbcType=VARCHAR}, #{userEmail,jdbcType=VARCHAR},
#{userPhone,jdbcType=VARCHAR},
#{userPassword,jdbcType=VARCHAR}, (SELECT NVL(MAX(SHOP_SEQ), 0) + 1 from USERTB),
#{userName,jdbcType=VARCHAR}, #{userAddress,jdbcType=VARCHAR}, #{userLevel,jdbcType=DECIMAL}
)
