mapper.update:
update SHOP
set SHOP_REGDATE = #{shopRegdate,jdbcType=TIMESTAMP}, SHOP_ADDRESS = #{shopAddress,jdbcType=VARCHAR}, SHOP_PHONE = #{shopPhone,jdbcType=VARCHAR}, SHOP_NAME = #{shopName,jdbcType=VARCHAR}
where SHOP_SEQ = #{shopSeq,jdbcType=DECIMAL}

mapper.select:
select SHOP_SEQ, SHOP_ADDRESS, SHOP_PHONE, SHOP_NAME, SHOP_REGDATE
from SHOP
where SHOP_SEQ = #{shopSeq,jdbcType=DECIMAL}
